import React from 'react'
import WeatherItem from './WeatherItem';

const API_key="157d33f8987d245bc6a1997408e90015"
const city = "Kielce"
const country = "PL"

class CurrentWeather extends React.Component{
    state={
        city:undefined,
        country:undefined,
        elements:[
            {id:'Clear',icon:'słonice'},
            {id:'Clouds',icon:'chmurka'},
            {id:'Rain',icon:'chmurka'},
            {id:'Thunderstorm',icon:'chmurka'},
            {id:'Drizzle',icon:'chmurka'},
            {id:'Atmosphere',icon:'chmurka'},
            {id:'Snow',icon:'chmurka'},
        ]
    }
    getWeather = async()=>{
    
          const api_call = await fetch(
            `http://api.openweathermap.org/data/2.5/weather?q=${city},${country}&APPID=${API_key}`
            );
      
          const response = await api_call.json();
      
          console.log(response)
          this.setState(
              {city:response.name,
                country:response.sys.country})
    }
    render(){
        return(
            <div>
                Weather App
                <td><button onClick={this.getWeather}>Pobierz pogode</button></td>
                <div className="card">{this.state.city},{this.state.country}</div>
            </div>
        )
    }
}

export default CurrentWeather
