import React,{useEffect, useState} from 'react'
import Axios from 'axios'
import UserPanel from '../UserPanel/UserPanel'
import {Link} from 'react-router-dom'
import './SignUp.css'

function SignUp(){

    const [usernameReg, setUsernameReg] = useState('')
    const [passwordReg, setPasswordReg] = useState('')

    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')

    const [loginStatus, setLoginStatus] = useState('')

    const [x, setX] = useState('')
    const [y, setY] = useState('')
    const [z, setZ] = useState('')

    Axios.defaults.withCredentials = true

    const register = () => {
        Axios.post('http://localhost:3001/api/register',{
            username:usernameReg,
            password:passwordReg
        }).then((response) =>{
            console.log(response)
        })
    }

    const login = () => {
        Axios.post('http://localhost:3001/api/login',{
            username:username,
            password:password
        }).then((response) =>{
            console.log(response)
            if(response.data.message){
                setLoginStatus(response.data.message)
            }else{
                setLoginStatus(response.data[0].username)
            }
        })
    }
    const registerBtn = () =>{
        setX("-400")
        setY("-400")
        setZ("110")
    }
    const loginBtn = () =>{
        setX("400")
        setY("450")
        setZ("0")
    }
    useEffect(()=>{
        Axios.get('http://localhost:3001/api/login').then((response)=>{
            if(response.data.loggedIn==true){
                setLoginStatus(response.data.user[0].username)
                console.log(response)
            }
        })
    },[])
        return(
            <div className="hero">
                <div className="form-box">
                    <div className="button-box">
                        <div className="btn"style={{
                                                    'transform':`translateX(${z}px)`
                                }}></div>
                        <button type="button" className="toggle-btn" onClick={() => loginBtn()}>Log In</button>
                        <button type="button" className="toggle-btn" onClick={() => registerBtn()}>Register</button>
                    </div>
                    <form className="login-input-group" style={{
                                                        'transform':`translateX(${x}px)`
                                }}>
                        <input type="text" className="input-field" placeholder="Nazwa Użytkownika" required/>
                        <input type="text" className="input-field" placeholder="Hasło" required/>
                        <input type="checkbox" className="check-box"/><span>Zapamietaj hasło</span>
                        <button type="submit" className="submit-btn">Zaloguj </button>
                    </form>
                    <form className="register-input-group"style={{
                                                        'transform':`translateX(${y}px)`
                                }}>
                        <input type="text" className="input-field" placeholder="Nazwa Użytkownika" required/>
                        <input type="email" className="input-field" placeholder="email" required/>
                        <input type="text" className="input-field" placeholder="Hasło" required/>
                        <button type="submit" className="submit-btn">Zarejestruj</button>
                    </form>
                </div>
            </div>

           
        )
    }
export default SignUp
