import React, {useState, useEffect} from 'react'
import './UserPanel.css';
import Axios from 'axios'
import UserWeatherItem from './UserWeatherItem';

function UserPanel(){
    const [cityName, setCityName] = useState('')
    const [weatherState, setWeatherState] = useState('')
    const [cityWeatherList, setcityWeatherList] = useState([])

    let  property = [{id:0}];

    useEffect(()=>{
        Axios.get('http://localhost:3001/api/get')
        .then((response)=>{
            setcityWeatherList(response.data)
        })

    },[])
    property =  cityWeatherList[0]
    console.log(property)

    const submitWeather = () =>{

        Axios.post('http://localhost:3001/api/insert',{
            cityName:cityName,weatherState:weatherState
        })

        setcityWeatherList([...cityWeatherList,{
                city:cityName,
                weather:weatherState
            },])
    }
    const nextProperty = () => {
        console.log(property)
        const newIndex = property.id
        console.log(newIndex)
        property = cityWeatherList[newIndex]
        console.log(property)
    }

    const prevProperty = () => {
        const newIndex = property.id
        property.id = cityWeatherList[newIndex]
    }
        return(
            <div className="main"style={{ 
                backgroundImage: `url("/images/bg_myWeather.jpg")` 
              }}>
            <div className='user-panel'>
                <h1>Moja Pogoda</h1>  
                <div className='user-form'>
                    <label>Miejsce</label>
                    <input type="text" name="cityName" onChange={(e)=>{
                        setCityName(e.target.value)
                    }}/>
                    <label>Pogoda</label>
                    <input type="text" name="weather"onChange={(e)=>{
                        setWeatherState(e.target.value)
                    }}/>

                    <button onClick={submitWeather}>Dodaj</button>
                </div>
                <div className="main-cards">
                    {console.log(property)}
                    <div className="cards-slider">
                        <button className="left" 
                                onClick={() => nextProperty()} 
                                disabled={property === cityWeatherList.length-1}
                                >Next
                        </button>
                        <div className="cards-slider-wrapper" style={{
                            'transform':`translateX(-${property*(100/cityWeatherList.length)}%)`
                        }}>
                            {cityWeatherList.map(fde => <UserWeatherItem key={fde.id} element={fde} />)}
                        </div>
                        <button className="right"
                                onClick={() => prevProperty()} 
                                disabled={property === 0}
                        >Prev
                        </button>
                    </div>

                </div>
            </div>
            </div>
        )
}
export default UserPanel
