import React from 'react'
import WeatherItem from './WeatherItem';
import Forecast from '../forecast/Forecast'

const API_key="157d33f8987d245bc6a1997408e90015"
//const city = "Kielce"
//const country = "PL"

class CurrentWeather extends React.Component{
    state={
        city:undefined,
        country:undefined,
        weather:undefined,
        temp:undefined,
        tempMax:undefined,
        tempMin:undefined,
        pressurre:undefined,
    }
    getWeather = async(e)=>{
        
        const city=e.target.elements.city.value;
        const country=e.target.elements.country.value;
          const api_call = await fetch(
            `http://api.openweathermap.org/data/2.5/weather?q=${this.state.city},${this.state.country}&APPID=${API_key}`
            );
      
          const response = await api_call.json();
      
          console.log(response)

          this.setState(
              {
                city:response.name,
                country:response.sys.country,
                weather:response.weather[0].description
              })
    }
    render(){
        
        return(
            <div>
                <forecast city={this.state.city} />
                <h1>Weather App</h1>
                <form onSubmit={props.loadweather}>
                <div className="row">
                <div className="col-md-3 offset-md-2">
                    <input type="text" className="form-control" name="city" autoComplete="off" placeholder="miasto"/>
                </div>
                <div className="col-md-3">
                <input type="text" className="form-control" name="country" autoComplete="off"placeholder="kraj"/>
                </div>
                <div className="col-md-3 mt-md 0 text-md-left">
                <button className="btn">Wyszukaj</button>
                </div>
            </div>
            </form>
                <div >
                    {this.state.city},{this.state.country}
                    <td>{this.state.weather}</td> 
                <div>
                <Forecast city={this.state.city}/> 
                </div>   
                </div>
            </div>
            
        )
    }
}

export default CurrentWeather
