import React from 'react'
import WeatherItem from './WeatherItem';
import Forecast from '../forecast/Forecast'
import Form from '../Form/Form'

const API_key="157d33f8987d245bc6a1997408e90015"
//const city = "Kielce"
//const country = "PL"

class CurrentWeather extends React.Component{
    state={
        value:"",
        city:"",
        country:"",
        weather:undefined,
        temp:undefined,
        tempMax:undefined,
        tempMin:undefined,
        pressurre:undefined,
        wind:undefined,
        error:false
    }
    calDate(time){
        var date = new Date(time * 1000);
        // Hours part from the timestamp
        var year = date.getFullYear();
        var month = date.getMonth()+1;
        var day = date.getDate();
        var daytime=day+'-'+0+month+'-'+year;
        return daytime;
      }
      calTime(time,timezone){
        var date = new Date(time*1000+timezone*1000-7200000);
        var hours = date.getHours();
        // Minutes part from the timestamp
        var minutes = date.getMinutes();
        // Seconds part from the timestamp
        var seconds = date.getSeconds();

        var daytime=hours+':'+minutes+':'+seconds;
        return daytime;
      }
    calCelsius(temp){
        let cell= Math.floor(temp-273.15)
        return cell;
    }
    calWindSpeed(wind){
        let cell= Math.floor(wind*3.6)
        return cell;
    }
    getWeather = (e) =>{
        
          
          fetch(`http://api.openweathermap.org/data/2.5/weather?q=${this.state.value}&APPID=${API_key}`)
          .then(response => {
              if(response.ok){
                  return response
              }
              throw Error("Błąd pobierania danych z API")
          })
          .then(response => response.json())
          .then(response => {
              const localTime = new Date().toLocaleString()
              console.log(response)
              this.setState(state =>({
                city:state.value,
                country:response.sys.country,
                date:this.calDate(response.dt),
                time:this.calTime(response.dt,response.timezone),
                weather:response.weather[0].description,
                temp:this.calCelsius(response.main.temp),
                tempMax:this.calCelsius(response.main.temp_max),
                tempMin:this.calCelsius(response.main.temp_min),
                pressure:response.main.pressure,
                wind:this.calWindSpeed(response.wind.speed),
                lat:response.coord.lat,
                lon:response.coord.lon, 
                error:false
              }))
          })
          .catch(err =>{
            console.log(err)
            this.setState(prevState =>{
                return{
                error:true,
                city:prevState.city
            }})
        })
    }
    inputHandler=(e)=>{
        this.setState({
            value:e.target.value
        })
    }
    submitHandler=(e)=>{
        this.getWeather(e)
    }
    render(){
        
        return(
            <div >
                <h1>Pogoda</h1>
                <div>
                <Form 
                value={this.state.value}  
                handler={this.inputHandler}
                submit={this.submitHandler}
                /> 
                </div> 
                <div>
                {this.state.city ?(
                    <WeatherItem weather={this.state}/>   
                ):null}
                </div>
                <div>
                {this.state.city ?(
                    <Forecast lat={this.state.lat} lon={this.state.lon}/>   
                ):null}
                 
                </div>   
            </div>
            
        )
    }
}
export default CurrentWeather
