import React from 'react'
import ForecastItem from './ForecastItem';
import CurrentWeather from '../currentWeather/CurrentWeather'

const API_key="157d33f8987d245bc6a1997408e90015"

class Forecast extends React.Component{
    state={
        elements:[
        ]
    }
    calCelsius(temp){
        let cell= Math.floor(temp-273.15)
        return cell;
    }
    calWindSpeed(wind){
        let cell= Math.floor(wind*3.6)
        return cell;
    }
    getForecast = async(props)=>{
    
          const api_call = await fetch(
            `https://api.openweathermap.org/data/2.5/onecall?lat=${this.props.lat}&lon=${this.props.lon}&appid=${API_key}`
            );
      
          const response = await api_call.json();
      
          console.log(response)
          for (var i = 0; i < 5; i++) {
            this.setState({
                elements:[...this.state.elements,{
                    'id':response.daily[i],
                    'weather':response.daily[i].weather[0].description,
                    'temp':this.calCelsius(response.daily[i].temp.day),
                    'pressure':response.daily[i].pressure,
                    'wind':this.calWindSpeed(response.daily[i].wind_speed),
                }],
            })
        } 
 
    }
    render(){
        const elements = this.state.elements.map(e=>{
            return <ForecastItem element={e}/>
        })
        console.log(this.props.lat)
        console.log(this.props.lon)
        return(
            <div>
                Forecast Comp:
                <td><button onClick={this.getForecast.bind(this)}>Pobierz dniowÄ… prognoze pogody</button></td>
                {elements}
            </div>
        )
    }
}

export default Forecast
