import React from 'react'
import WeatherItem from './WeatherItem';
import Forecast from '../forecast/Forecast'
import Form from '../Form/Form'
import CalDate from '../Functions/CalDate';
import CalTime from '../Functions/CalTime';
import CalCelsius from '../Functions/CalCelsius';
import CalWindSpeed from '../Functions/CalWindSpeed';

const API_key="157d33f8987d245bc6a1997408e90015"

class CurrentWeather extends React.Component{
    
    state={
        value:"",
        city:"",
        country:"",
        weather:undefined,
        temp:undefined,
        tempMax:undefined,
        tempMin:undefined,
        pressurre:undefined,
        wind:undefined,
        error:false
    }
    
    getWeather = (e) =>{
        
        e.preventDefault()
          
          fetch(`http://api.openweathermap.org/data/2.5/weather?q=${this.state.value}&APPID=${API_key}`)
          .then(response => {
              if(response.ok){
                  return response
              }
              throw Error("Błąd pobierania danych z API")
          })
          .then(response => response.json())
          .then(response => {
              const localTime = new Date().toLocaleString()
              console.log(response)
              this.setState(state =>({
                city:state.value,
                country:response.sys.country,
                date:CalDate(response.dt),
                time:CalTime(response.dt,response.timezone),
                weather:response.weather[0].description,
                temp:CalCelsius(response.main.temp),
                tempMax:CalCelsius(response.main.temp_max),
                tempMin:CalCelsius(response.main.temp_min),
                pressure:response.main.pressure,
                wind:CalWindSpeed(response.wind.speed),
                lat:response.coord.lat,
                lon:response.coord.lon, 
                error:false
              }))
          })
          .catch(err =>{
            console.log(err)
            this.setState(prevState =>{
                return{
                error:true,
                city:prevState.city
            }})
        })
    }
    inputHandler=(e)=>{
        this.setState({
            value:e.target.value
        })
    }
    submitHandler=(e)=>{
        this.getWeather(e)
        return(
            <Forecast lat={this.state.lat} lon={this.state.lon}/>
        )
    }
    render(){
        
        return(
            <div >
                <h1>Pogoda</h1>
                <div>
                <Form 
                value={this.state.value}  
                handler={this.inputHandler}
                submit={this.submitHandler}
                /> 
                </div> 
                <div>
                {this.state.city ?(
                    <WeatherItem weather={this.state}/>   
                ):null}
                </div>
                <div>
                {this.state.city ?(
                    <Forecast lat={this.state.lat} lon={this.state.lon}/>   
                ):null}
                 
                </div>   
            </div>
            
        )
    }
}
export default CurrentWeather
