import React from 'react'
import WeatherItem from './WeatherItem';
import Forecast from '../forecast/Forecast'

const API_key="157d33f8987d245bc6a1997408e90015"
//const city = "Kielce"
//const country = "PL"

class CurrentWeather extends React.Component{
    state={
        city:undefined,
        country:undefined,
        weather:undefined,
        temp:undefined,
        tempMax:undefined,
        tempMin:undefined,
        pressurre:undefined,
    }
    getWeather(){
        
          fetch(`http://api.openweathermap.org/data/2.5/weather?q=${this.state.city},${this.state.country}&APPID=${API_key}`)
          .then(response => response.json())
          .then(data => this.setState({
                city:data.name,
                country:data.sys.country,
                weather:data.weather[0].description
          }))
          .catch(err => console.log(err))
    }
    inputCityHandler(event){
        const newCity = event.target.ref.city.value
        this.setState({city:newCity})
    }
    inputCountryHandler(event){
        const newCountry = event.target.value
        this.setState({country:newCountry})
    }
    render(){
        
        return(
            <div>
                <forecast city={this.state.city} />
                <h1>Weather App</h1>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        Imię:
                        <input type="text" ref={this.input} />
                    </label>
                    <input type="submit" value="Wyślij" />
                </form>
                <input type="text" ref="city" value={this.state.cityName} onChange={this.inputCityHandler.bind(this)}></input>
                <input type="text" value={this.state.CountryName} onChange={this.inputCountryHandler.bind(this)}></input>
                <td><button onClick={this.getWeather.bind(this)}>Pobierz aktualną pogode</button></td>
                <div >
                    {this.state.city},{this.state.country}
                    <td>{this.state.weather}</td> 
                <div>
                <Forecast city={this.state.city}/> 
                </div>   
                </div>
            </div>
            
        )
    }
}

export default CurrentWeather
