import React from 'react'
import CurrentWeather from '../currentWeather/CurrentWeather';
import WeatherItem from './ForecastItem';

const API_key="157d33f8987d245bc6a1997408e90015"
const city = "Kielce"
const country = "PL"

class Forecast extends React.Component{
    state={
        elements:[
            {id:'1',title:'title1'},
            {id:'2',title:'title2'}
        ]
    }
    getWeather = async()=>{
    
          const api_call = await fetch(
            `http://api.openweathermap.org/data/2.5/weather?q=${CurrentWeather.state.city},${CurrentWeather.state.country}&APPID=${API_key}`
            );
      
          const response = await api_call.json();
      
          console.log(response)
          console.log(CurrentWeather.state.city)
          this.setState({title:response.name})
    }
    render(){
        const elements = this.state.elements.map(e=>{
            return <WeatherItem element={e}/>
        })
        return(
            <div>
                Weather App
                {elements}
            </div>
        )
    }
}

export default Forecast
