import React from 'react'
import ForecastDailyItem from './ForecastDailyItem';
import ForecastHourlyItem from './ForecastHourlyItem';
import CurrentWeather from '../currentWeather/CurrentWeather'

const API_key="157d33f8987d245bc6a1997408e90015"

class Forecast extends React.Component{
    state={
        elements:[
        ],
        elements2:[
        ]
    }
    calDate(time){
        var date = new Date(time * 1000);
        // Hours part from the timestamp
        var year = date.getFullYear();
        var month = date.getMonth()+1;
        var day = date.getDate();
        var daytime=day+'-'+0+month+'-'+year;
        return daytime;
      }
      calTime(time){
        var date = new Date(time*1000);
        var hours = date.getHours();
        // Minutes part from the timestamp
        var minutes = date.getMinutes();
        // Seconds part from the timestamp
        var seconds = date.getSeconds();
    
        var daytime=hours+':'+minutes+':'+seconds;
        console.log(time)
        return daytime;
      }
    calCelsius(temp){
        let cell= Math.floor(temp-273.15)
        return cell;
    }
    calWindSpeed(wind){
        let cell= Math.floor(wind*3.6)
        return cell;
    }
    getForecastDaily = async(props)=>{
    
          const api_call = await fetch(
            `https://api.openweathermap.org/data/2.5/onecall?lat=${this.props.lat}&lon=${this.props.lon}&appid=${API_key}`
            );
      
          const response = await api_call.json();
      
          console.log(response)
          for (var i = 1; i < 8; i++) {
            this.setState({
                elements:[...this.state.elements,{
                    'id':response.daily[i],
                    'date':this.calDate(response.daily[i].dt),
                    'weather':response.daily[i].weather[0].description,
                    'temp':this.calCelsius(response.daily[i].temp.day),
                    'pressure':response.daily[i].pressure,
                    'wind':this.calWindSpeed(response.daily[i].wind_speed),
                }],
            })
        }
    }
    getForecastHourly = async(props)=>{
    
        const api_call = await fetch(
          `https://api.openweathermap.org/data/2.5/onecall?lat=50.8703&lon=20.6275&appid=${API_key}`
          );
    
        const response = await api_call.json();
    
      for (var i = 0; i < 48; i++) {
          this.setState({
              elements2:[...this.state.elements2,{
                  'id':response.hourly[i],
                  'date':this.calDate(response.hourly[i].dt),
                  'time':this.calTime(response.hourly[i].dt),
                  'weather':response.hourly[i].weather[0].description,
                  'temp':this.calCelsius(response.hourly[i].temp),
                  'pressure':response.hourly[i].pressure,
                  'wind':this.calWindSpeed(response.hourly[i].wind_speed),
              }],
          })
      }   
  }
  componentDidMount(){ this.getForecastHourly() }
    render(){
        const elements = this.state.elements.map(e=>{
            return <ForecastDailyItem element={e}/>
        })
        const elements2 = this.state.elements2.map(e=>{
            return <ForecastHourlyItem element={e}/>
        })
        console.log(this.props.lat)
        console.log(this.props.lon)
        return(
            <div >
                <div className="card2">
                <td><button onClick={this.getForecastDaily.bind(this)}>Dniowa</button></td>
                {elements}
                </div>
                <div className="card2">
                <td><button onClick={this.getForecastHourly.bind(this)}>Godzinowa</button></td>
                {elements2}
                </div>
            </div>
        )
    }
}

export default Forecast
