import React from 'react'
import WeatherItem from './ForecastItem';
import CurrentWeather from '../currentWeather/CurrentWeather'

const API_key="157d33f8987d245bc6a1997408e90015"
const city = "Kielce"
const country = "PL"

class Forecast extends React.Component{
    state={
        elements:[
            {id:'1',title:'title1'},
            {id:'2',title:'title2'}
        ]
    }
    getForecast = async(props)=>{
    
          const api_call = await fetch(
            `http://api.openweathermap.org/data/2.5/weather?q=${city},${country}&APPID=${API_key}`
            );
      
          const response = await api_call.json();
      
          console.log(response)
          this.setState({title:response.name})
    }
    render(){
        const elements = this.state.elements.map(e=>{
            return <WeatherItem element={e}/>
        })
        console.log(this.props.city)
        console.log(this.props.country)
        return(
            <div>
                Weather App
                <td><button onClick={this.getForecast.bind(this)}>Pobierz prognoze pogody</button></td>
                {elements}
            </div>
        )
    }
}

export default Forecast
