import React from 'react'
import WeatherItem from './WeatherItem';
import Forecast from '../forecast/Forecast'
import Form from '../Form/Form'
import CalDate from '../Functions/CalDate';
import CalTime from '../Functions/CalTime';
import CalCelsius from '../Functions/CalCelsius';
import CalWindSpeed from '../Functions/CalWindSpeed';
import './CurrentWeather.css';

const API_key="157d33f8987d245bc6a1997408e90015"

class CurrentWeather extends React.Component{
    
    state={
        value:"",
        city:"Kielce",
        image:undefined,
        weather:undefined,
        temp:undefined,
        tempMax:undefined,
        tempMin:undefined,
        pressurre:undefined,
        wind:undefined,
        lat:undefined,
        lon:undefined,
        error:false
    }
    
    getWeather = (e) =>{
        
        e.preventDefault()
          
          fetch(`http://api.openweathermap.org/data/2.5/weather?q=${this.state.value}&lang=pl&APPID=${API_key}`)
          .then(response => {
              if(response.ok){
                  return response
              }
              throw Error("Błąd pobierania danych z API")
          })
          .then(response => response.json())
          .then(response => {
              const localTime = new Date().toLocaleString()
              console.log(response)
              this.setState(state =>({
                city:state.value,
                country:response.sys.country,
                date:CalDate(response.dt),
                time:CalTime(response.dt,response.timezone),
                weather:response.weather[0].description,
                temp:CalCelsius(response.main.temp),
                tempMax:CalCelsius(response.main.temp_max),
                tempMin:CalCelsius(response.main.temp_min),
                pressure:response.main.pressure,
                wind:CalWindSpeed(response.wind.speed),
                lat:response.coord.lat,
                lon:response.coord.lon, 
                error:false
              }))
              if(response.weather[0].id >=200 && response.weather[0].id <=232){
                  this.setState({
                      image:weatherIcons[200]
                  })
              }
              if(response.weather[0].id >=300 && response.weather[0].id <=321){
                this.setState({
                    image:weatherIcons[300]
                })
              }
              if(response.weather[0].id >=500 && response.weather[0].id <=531){
                this.setState({
                    image:weatherIcons[500]
                })
              }
              if(response.weather[0].id >=600 && response.weather[0].id <=622){
                this.setState({
                    image:weatherIcons[600]
                })
              }
              if(response.weather[0].id >=700 && response.weather[0].id <=781){
                this.setState({
                    image:weatherIcons[700]
                })
              }
              if(response.weather[0].id ==800){
                this.setState({
                    image:weatherIcons[800]
                })
              }
              if(response.weather[0].id >=800 && response.weather[0].id <=804){
                this.setState({
                    image:weatherIcons[801]
                })
              }
          })
          .catch(err =>{
            console.log(err)
            this.setState(prevState =>{
                return{
                error:true,
                city:prevState.city
            }})
        })
       
    }
    inputHandler=(e)=>{
        this.setState({
            value:e.target.value
        })
    }
    submitHandler=(e)=>{
        this.getWeather(e)
    }
    render(){
        return(
            <div >
                <div className="city-form">
                <Form 
                value={this.state.value}  
                handler={this.inputHandler}
                submit={this.submitHandler}
                /> 
                </div> 
                <div>
                {this.state.city ?(
                    <WeatherItem weather={this.state}/>   
                ):null}
                </div>
                <div>
                {this.state.city ?(
                    <Forecast lat={this.state.lat} lon={this.state.lon}/>   
                ):null}
                 
                </div>   
            </div>
            
        )
    }
}
