import React from 'react'
import WeatherItem from './WeatherItem';
import Forecast from '../forecast/Forecast'

const API_key="157d33f8987d245bc6a1997408e90015"
//const city = "Kielce"
//const country = "PL"

class CurrentWeather extends React.Component{
    state={
        city:undefined,
        country:undefined,
        weather:undefined,
        temp:undefined,
        tempMax:undefined,
        tempMin:undefined,
        pressurre:undefined,
        wind:undefined,
    }
    calDate(time){
        var date = new Date(time * 1000);
        // Hours part from the timestamp
        var year = date.getFullYear();
        var month = date.getMonth()+1;
        var day = date.getDate();
        var daytime=day+'-'+0+month+'-'+year;
        return daytime;
      }
      calTime(time){
        var date = new Date(time*1000);
        var hours = date.getHours();
        // Minutes part from the timestamp
        var minutes = date.getMinutes();
        // Seconds part from the timestamp
        var seconds = date.getSeconds();
    
        var daytime=hours+':'+minutes+':'+seconds;
        console.log(date)
        return daytime;
      }
    calCelsius(temp){
        let cell= Math.floor(temp-273.15)
        return cell;
    }
    calWindSpeed(wind){
        let cell= Math.floor(wind*3.6)
        return cell;
    }
    getWeather = async()=>{
        
          const api_call = await fetch(
            `http://api.openweathermap.org/data/2.5/weather?q=${this.state.city},${this.state.country}&APPID=${API_key}`
            );
      
          const response = await api_call.json();
      
          console.log(response)

          this.setState(
              {
                city:response.name,
                country:response.sys.country,
                date:this.calDate(response.coord.dt),
                time:this.calTime(response.coord.dt),
                weather:response.weather[0].description,
                temp:this.calCelsius(response.main.temp),
                tempMax:this.calCelsius(response.main.temp_max),
                tempMin:this.calCelsius(response.main.temp_min),
                pressure:response.main.pressure,
                wind:this.calWindSpeed(response.wind.speed),
                lat:response.coord.lat,
                lon:response.coord.lon, 
              })
    }
    inputCityHandler(event){
        const newCity = event.target.value
        this.setState({city:newCity})
    }
    inputCountryHandler(event){
        const newCountry = event.target.value
        this.setState({country:newCountry})
    }
    render(){
        
        return(
            <div>
                <h1>Weather App</h1>
                <input type="text" value={this.state.cityName} onChange={this.inputCityHandler.bind(this)}></input>
                <input type="text" value={this.state.CountryName} onChange={this.inputCountryHandler.bind(this)}></input>
                <td><button onClick={this.getWeather.bind(this)}>Pobierz aktualną pogode</button></td>
                <div >
                    {this.state.city},{this.state.country}
                    <td>{this.state.day}</td>
                    {this.state.time}
                    <td>{this.state.weather}</td>
                    Aktualna temperatura: {this.state.temp}&deg;
                    <td>Temperatura maksymalna:{this.state.tempMax}&deg;</td>  
                    Temperatura minimalna:{this.state.tempMin}&deg;
                    <td>Ciśnienie:{this.state.pressure} hPa</td>  
                    Wiatr:{this.state.wind}km/h
                <div>
                <Forecast lat={this.state.lat} lon={this.state.lon}/> 
                </div>   
                </div>
            </div>
            
        )
    }
}

export default CurrentWeather
