import React from 'react'

class CurrentWeatherFunctions extends React.Component{

    calDate(time){

        var date = new Date(time * 1000);
        var year = date.getFullYear();
        var month = date.getMonth()+1;
        var day = date.getDate();

        var daytime = day + '-' + 0 + month + '-' + year;

        return daytime;
    }

    calTime(time,timezone){

        var date = new Date(time*1000+timezone*1000-7200000);
        var hours = date.getHours();
        var minutes = date.getMinutes();
        var seconds = date.getSeconds();

        var daytime = hours + ':' + minutes + ':' + seconds;

        return daytime;
    }

    calCelsius(temp){

        let cell= Math.floor(temp-273.15)

        return cell;
    }

    calWindSpeed(wind){

        let cell= Math.floor(wind*3.6)

        return cell;
    }

}
export default CurrentWeatherFunctions
