import React from 'react'
import ForecastDailyItem from './ForecastDailyItem';
import ForecastHourlyItem from './ForecastHourlyItem';
import CurrentWeather from '../currentWeather/CurrentWeather'
import './Forecast.css';
import CalDate from '../Functions/CalDate';
import CalCelsius from '../Functions/CalCelsius';
import CalWindSpeed from '../Functions/CalWindSpeed';
import CalTime from '../Functions/CalTime';
const API_key="157d33f8987d245bc6a1997408e90015"

class Forecast extends React.Component{
    constructor(props){
        super(props)
        this.state={
            elements:[
            ],
            elements2:[
            ],
            property: undefined,
            property2: undefined
        }
    }

    nextProperty = () => {
        const newIndex = this.state.property.id +1
        this.setState({
            property: this.state.elements[newIndex]
        })
    }

    prevProperty = () => {
        const newIndex = this.state.property.id -1
        this.setState({
            property: this.state.elements[newIndex]
        })
    }

    nextProperty2 = () => {
        const newIndex = this.state.property2.id +1
        this.setState({
            property2: this.state.elements2[newIndex]
        })
    }
    
    prevProperty2 = () => {
        const newIndex = this.state.property2.id -1
        this.setState({
            property2: this.state.elements2[newIndex]
        })
    }
    
    getForecastDaily = ()=>{
        this.state.elements.length=0
        fetch(`https://api.openweathermap.org/data/2.5/onecall?lat=${this.props.lat}&lon=${this.props.lon}&appid=${API_key}`)
          .then(response => {
              if(response.ok){
                  return response
              }
              throw Error("Błąd pobierania danych z API")
          })
          .then(response => response.json())
          .then(response => {
              const localTime = new Date().toLocaleString()
              console.log(response)
              for (var i = 0; i < 8; i++) {
                this.setState({
                    elements:[...this.state.elements,{
                        'id':i,
                        'date':CalDate(response.daily[i].dt),
                        'weather':response.daily[i].weather[0].description,
                        'temp':CalCelsius(response.daily[i].temp.day),
                        'pressure':response.daily[i].pressure,
                        'wind':CalWindSpeed(response.daily[i].wind_speed),
                    }],
                    property:this.state.elements[0]
                })
            }   
          })
        console.log(this.property)
    }
    getForecastHourly = (props)=>{
        this.state.elements2.length=0
        fetch(`https://api.openweathermap.org/data/2.5/onecall?lat=${this.props.lat}&lon=${this.props.lon}&appid=${API_key}`)
          .then(response => {
              if(response.ok){
                  return response
              }
              throw Error("Błąd pobierania danych z API")
          })
          .then(response => response.json())
          .then(response => {
              const localTime = new Date().toLocaleString()
              console.log(response)
              for (var i = 0; i < 48; i++) {
                this.setState({
                    elements2:[...this.state.elements2,{
                        'id':i,
                        'date':CalDate(response.hourly[i].dt),
                        'time':CalTime(response.hourly[i].dt,response.timezone_offset),
                        'weather':response.hourly[i].weather[0].description,
                        'temp':CalCelsius(response.hourly[i].temp),
                        'pressure':response.hourly[i].pressure,
                        'wind':CalWindSpeed(response.hourly[i].wind_speed),
                    }],
                    property2:this.state.elements2[0]
                })
            }
          })
    }
    componentDidMount(){
       this.getForecastDaily()
       this.getForecastHourly()  
    }
    render(){
        const {elements, elements2, property, property2}=this.state

        return(
            <div className="main">
            
                <button onClick={ this.getForecastDaily.bind(this) }>Refresh Component</button>

                <button onClick={ this.getForecastHourly.bind(this) }>Refresh Component</button>

                {property ?(
                <button 
                onClick={() => this.nextProperty()} 
                disabled={property.id === elements.length-1}
                >Next</button>
                ):null}
                
                {property ?(
                <button 
                onClick={() => this.prevProperty()} 
                disabled={property.id === 0}
                >Prev</button>
                ):null}

                {property2 ?(
                <button 
                onClick={() => this.nextProperty2()} 
                disabled={property2.id === elements2.length-1}
                >Next</button>
                ):null}
                
                {property2 ?(
                <button 
                onClick={() => this.prevProperty2()} 
                disabled={property2.id === 0}
                >Prev</button>
                ):null}

                {property ?(
                <div className="cards-slider">
                <div className="cards-slider-wrapper" style={{
                    'transform':`translateX(-${property.id*(100/elements.length)}%)`
                }}>
                {elements.map(elements => <ForecastDailyItem key={elements.id} element={elements} />)}
                </div>
                </div>
                ):null}
                
                {property2 ?(
                <div className="cards-slider2">
                <div className="cards-slider-wrapper2" style={{
                    'transform':`translateX(-${property2.id*(100/elements2.length)}%)`
                }}>
                {elements2.map(elements2 => <ForecastHourlyItem key={elements2.id} element={elements2} />)}
                </div>
                </div>
                ):null}
            </div>

    
        )
    }
}

export default Forecast
