import React from 'react'
import WeatherItem from './WeatherItem';
import Forecast from '../forecast/Forecast'

const API_key="157d33f8987d245bc6a1997408e90015"
//const city = "Kielce"
//const country = "PL"

class CurrentWeather extends React.Component{
    state={
        city:undefined,
        country:undefined,
        weather:undefined,
        temp:undefined,
        tempMax:undefined,
        tempMin:undefined,
        pressurre:undefined,
    }
    getWeather = async(e)=>{
        
          const api_call = await fetch(
            `http://api.openweathermap.org/data/2.5/weather?q=${this.state.city},${this.state.country}&APPID=${API_key}`
            );
      
          const response = await api_call.json();
      
          console.log(response)

          this.setState(
              {
                city:response.name,
                country:response.sys.country,
                weather:response.weather[0].description
              })
    }
    render(){
        
        return(
            <div>
                <forecast city={this.state.city} />
                <h1>Weather App</h1>
                <input type="text" value={this.state.cityName} onChange={this.getWeather} type="text" ref="city"></input>
                <input type="text" value={this.state.CountryName} onChange={this.getWeather}></input>
                <td><button onClick={this.getWeather.bind(this)}>Pobierz aktualnÄ… pogode</button></td>
                <div >
                    {this.state.city},{this.state.country}
                    <td>{this.state.weather}</td> 
                <div>
                <Forecast city={this.state.city}/> 
                </div>   
                </div>
            </div>
            
        )
    }
}

export default CurrentWeather
