import React from 'react'
import Form from '../Form/Form'
import CalDate from '../Functions/CalDate';
import CalTime from '../Functions/CalTime';

const API_key="157d33f8987d245bc6a1997408e90015"


class WeatherAlerts extends React.Component{
    state={
        value:"",
        city:"",
        country:"",
        description:"",
        end:undefined,
        event:"",
        sender_name:"",
        start:undefined,
        error:false
    }
    calDate(time){
        var date = new Date(time * 1000);
        // Hours part from the timestamp
        var year = date.getFullYear();
        var month = date.getMonth()+1;
        var day = date.getDate();
        var daytime=day+'-'+0+month+'-'+year;
        return daytime;
      }
      calTime(time,timezone){
        var date = new Date(time*1000+timezone*1000-7200000);
        var hours = date.getHours();
        // Minutes part from the timestamp
        var minutes = date.getMinutes();
        // Seconds part from the timestamp
        var seconds = date.getSeconds();

        var daytime=hours+':'+minutes+':'+seconds;
        return daytime;
      }
    getAir = (e) =>{
        
        e.preventDefault()
          
          fetch(`http://api.openweathermap.org/data/2.5/weather?q=${this.state.value}&APPID=${API_key}`)
          .then(response => {
              if(response.ok){
                  return response
              }
              throw Error("Błąd pobierania danych z API")
          })
          .then(response => response.json())
          .then(response => {
              const localTime = new Date().toLocaleString()
              console.log(response)
              this.setState(state =>({
                city:state.value,
                country:response.sys.country,
                date:CalDate(response.dt),
                time:CalTime(response.dt,response.timezone),
                lat:response.coord.lat,
                lon:response.coord.lon, 
                error:false
              }))
              fetch(`https://api.openweathermap.org/data/2.5/onecall?lat=${response.coord.lat}&lon=${response.coord.lon}&lang=pl&appid=${API_key}`)
          .then(response2 => {
              if(response2.ok){
                  return response2
              }
              throw Error("Błąd pobierania danych z API")
          })
          .then(response2 => response2.json())
          .then(response2 => {
              console.log(response2)
              if(response2.alerts){
                this.setState(state =>({
                        description:response2.alerts[0].description,
                        error:false
                      }))   
              }else{
                this.setState(state =>({
                        description:"brak alertów",
                        error:false
                      }))
              }
          })
          .catch(err =>{
            console.log(err)
            this.setState(prevState =>{
                return{
                error:true,
                city:prevState.city
            }})
        })
          })
          .catch(err =>{
            console.log(err)
            this.setState(prevState =>{
                return{
                error:true,
                city:prevState.city
            }})
        })
    }
    inputHandler=(e)=>{
        this.setState({
            value:e.target.value
        })
    }
    submitHandler=(e)=>{
        this.getAir(e)
    }
    render(){
        
        return(
            <div >
                <h1>Alrty Pogodowe</h1>
                <div>
                <Form 
                value={this.state.value}  
                handler={this.inputHandler}
                submit={this.submitHandler}
                /> 
                </div> 
                <div>
                        <p>{this.state.city}</p>
                        <p>{this.state.country}</p>
                        <p>{this.state.date}</p>
                        <p>{this.state.time}</p> 
                        <p>{this.state.description}</p> 
                </div>
            </div>
            
        )
    }
}
export default WeatherAlerts
