import React from 'react'
import WeatherItem from './WeatherItem';

const API_key="157d33f8987d245bc6a1997408e90015"
//const city = "Kielce"
//const country = "PL"

class CurrentWeather extends React.Component{
    state={
        city:undefined,
        country:undefined,
        weather:undefined,
        temp:undefined,
        tempMax:undefined,
        tempMin:undefined,
        pressurre:undefined,
        elements:[
            {id:'Clear',icon:'słonice'},
            {id:'Clouds',icon:'chmurka'},
            {id:'Rain',icon:'chmurka'},
            {id:'Thunderstorm',icon:'chmurka'},
            {id:'Drizzle',icon:'chmurka'},
            {id:'Atmosphere',icon:'chmurka'},
            {id:'Snow',icon:'chmurka'},
        ],
    }
    getWeather = async(event)=>{
    
        const city=event.target.value
        const country=event.target.elements.country.value;

          const api_call = await fetch(
            `http://api.openweathermap.org/data/2.5/weather?q=${city},${country}&APPID=${API_key}`
            );
      
          const response = await api_call.json();
      
          console.log(response)

          this.setState(
              {
                city:response.name,
                country:response.sys.country,
                weather:response.weather[0].description
              })
    }
    inputHandler(event){
        const newCity = event.target.value
        this.setState({city:newCity})
    }
    render(){
        return(
            <div>
                <h1>Weather App</h1>
                <input type="text" value={this.state.cityName} onChange={this.inputHandler.bind(this)}></input>
                <td><button onClick={this.getWeather.bind(this)}>Pobierz aktualną pogode</button></td>
                <div className="card">
                    {this.state.city},{this.state.country}
                    <td>{this.state.weather}</td>
                </div>
            </div>
        )
    }
}

export default CurrentWeather
